mapper.select:
SELECT COUNT(*)
FROM TBL_USER
WHERE USER_ID = #{userId} AND USER_PW = #{userPw}

SELECT COUNT(*)
FROM TBL_USER
WHERE USER_NICK = #{userNick}

mapper.update:
UPDATE TBL_USER
SET
USER_NICK = #{userNick}
WHERE USER_ID = #{userID};

UPDATE TBL_USER
SET USER_PW =
#{userPw}
WHERE USER_ID = #{userId};

UPDATE TBL_USER
SET
USER_PHONE = #{userPhone}
WHERE USER_ID = #{userId};
